import React,{useEffect} from 'react'
import './App.css'
import Navbar from './components/Navbar/Navbar'
import Landing from './components/landing/Landing'
import Poster from './components/posters/Poster'
import posterData from './components/posters/posterData'
import axios from "./axios"
import { useState } from 'react'
import { API_KEY } from './constants/Constants'
// import {API_KEY} from "./constants/Constants"

function App() {
   const[trendingMovies, setTrendingMoviesData] = useState()
   console.log(trendingMovies ? trendingMovies : "","trending movies")

	const PosterItems = posterData.map((item) => <Poster key={item.id} src={item.src} />)



	useEffect(() => {

axios.get(
      `https://api.themoviedb.org/3/trending/all/day?api_key=${API_KEY}`
    ).then((res) =>{setTrendingMoviesData(res.data)})

	}, [])



	return (
    <div>
      <Navbar />
      <Landing />

      <div className="rowpost">
        <p className="movie mt-3" style={{ color: "white" }}>
          Movies
        </p>
        <div className="posters">
          <div className="poster">{PosterItems}</div>
        </div>
      </div>
      {/* trending movies */}
      <div className="rowpost">
        <p className="movie mt-3" style={{ color: "white" }}>
          Trending Movies
        </p>
        <div className="posters">
					<div className="poster">
						{
							trendingMovies && trendingMovies.results.map((item, index) => (
    <Poster key={index} src={`https://image.tmdb.org/t/p/w500/${item.poster_path}` }/>
  ))	
						}
					</div>
        </div>
      </div>
    </div>
  );
}

export default App
