import React,{useEffect,useState} from 'react'
import './Landing.css'
import axios from 'axios'
import { API_KEY } from '../../constants/Constants'


function Landing() {
  const [landMovies,setLandMovies] = useState([])
  console.log("landMOvies",landMovies)

    useEffect(() => {
        axios
          .get(
            `https://api.themoviedb.org/3/trending/all/day?api_key=${API_KEY}`
          )
          .then((res) => {
            setLandMovies(res.data.results);
          });
    }, [])
    return (
      <div
        className="land"
        style={{
          background: `url('https://image.tmdb.org/t/p/w100/${
            landMovies.length !== 0 ? landMovies[8].backdrop_path : ""
          }')`, backgroundRepeat: "no-repeat",
        }}
      >
        <h1>
          Unlimited movies, TV<br></br> shows and more.
        </h1>
        <p className="sub-head">Watch anywhere. Cancel anytime.</p>
        <p className="para">
          Ready to watch? Enter your email to create or restart your membership.
        </p>
        <div className="form">
          <input type="email" placeholder="Email-Address"></input>
          <button className="submit-btn">
            Get Started <i></i>
          </button>
        </div>
      </div>
    );
}

export default Landing
