import './App.css'
import React from 'react';
import { isCompositeComponentWithType } from 'react-dom/test-utils';
class App extends React.Component {
constructor(props) {
	super(props);

	// Initializing the state
	this.state = { color: 'lightgreen',backgroundColor: 'red' };
}
componentDidMount() {

	// Changing the state after 2 sec
	// from the time when the component
	// is rendered
	setTimeout(() => {
	this.setState({ color: 'blue',backgroundColor: 'orange' });
	},2500);
}

componentWillUnmount() {
	this.setState({color: 'white', backgroundColor : 'red'})
}
render() {
	return (
	<div className ="react-anima">
		<p
		style={{
			color: this.state.color,
			backgroundColor: this.state.backgroundColor,
			textAlign: 'center',
			paddingTop: 20,
			width: 250,
			height: 250,
			margin: 'auto'

		}}
		>

		</p>

	</div>
	);
}
}
export default App;
