import React,{useEffect} from 'react'
import './App.css'
import Navbar from './components/Navbar/Navbar'
import Landing from './components/landing/Landing'
import Poster from './components/posters/Poster'
import posterData from './components/posters/posterData'
import axios from "./axios"
import { useState } from 'react'
// import {API_KEY} from "./constants/Constants"

function App() {
   const[trendingMovies, setTrendingMoviesData] = useState()
   console.log(trendingMovies,"trending movies")

	const PosterItems = posterData.map((item) => <Poster key={item.id} src={item.src} />)

	const getTmdbApi = async () => {
		await axios.get(
      "https://api.themoviedb.org/3/trending/all/day?api_key=f0bde51db52e9c6388d864e6ab433ac6"
    ).then((res) =>{setTrendingMoviesData(res.data)})
	}

	useEffect(() => {

getTmdbApi();	
		return () => {
getTmdbApi();		}
	}, [])


	return (
		<div>
			<Navbar />
			<Landing />

			<div className ="rowpost">
				<p className ="movie mt-3" style ={{color: 'white'}}>Movies</p>
            <div className="posters">
                <div  className="poster">
				   {PosterItems}
                </div>
            </div>
        </div>		    
		</div>
	)
}

export default App
